#!/bin/bash
: "
This script is used to send Teams notification before & after deploy.
The Teams's AdaptiveCard does not work, so use of Teams's MessageCard instead.
"

# Get current timestamp.
CURRENT_TIMESTAMP=$(date +"%s")

# Get current directory.
DIRECTORY=$(cd -- "$(dirname -- "${BASH_SOURCE[0]}")" &> /dev/null && pwd)

# Human readable variables from arguments.
WEBHOOK_URL="$1";
CI_JOB_STATUS="$2";
CI_JOB_NAME="$3";
GITLAB_USER_NAME="$4";
CI_JOB_STARTED_AT="$5";
CI_JOB_URL="$6";
CI_JOB_MANUAL="$7";

# Default variables.
ACTIVITY_IMAGE=""
ACTIVITY_SUBTITLE=""
AUTHOR="$GITLAB_USER_NAME"
COLOR=""
DATE=$(TZ=Europe/Paris date -d "$CI_JOB_STARTED_AT" +%d/%m/%Y\ -\ %H:%M)
DEPLOY_TYPE="Manuel"
DURATION_TIMESTAMP=$(("$CURRENT_TIMESTAMP" - $(date +%s -d "$CI_JOB_STARTED_AT")))
DURATION=$(date -d@"$DURATION_TIMESTAMP" -u +%H:%M:%S)
ENV=""
JIRA_TITLE=""
JIRA_SUBTITLE=""
LOGO_DRUPAL="data:image/png;base64,"
LOGO_KEYCLOAK="data:image/png;base64,"
LOGO_SANTE="data:image/png;base64,"
STATE=""
TEMPLATE="$DIRECTORY/templates/$CI_JOB_STATUS.json"
US_LIST=""

# Set dynamique variables depending on CI job status.
set_variables () {
  # Status variables.
  case "$CI_JOB_STATUS" in
  canceled)
    ACTIVITY_TITLE="Un d√©ploiement a √©t√© annul√© üöß"
    COLOR="D700C9"
    STATE="Annul√©"
    ;;
  failed)
    ACTIVITY_TITLE="Un d√©ploiement est en erreur ‚ùå"
    COLOR="D70000"
    STATE="Erreur"
    ;;
  start)
    ACTIVITY_TITLE="Un d√©ploiement est en cours üöÄ"
    COLOR="FCA326"
    ;;
  success)
    ACTIVITY_TITLE="Un d√©ploiement est termin√© ‚úîÔ∏è"
    COLOR="16D700"
    STATE="Succ√®s"
    ;;
  esac

  # Environment variables.
  case "$CI_JOB_NAME" in
  # SANTE RUI
  deploy_rui_sante)
    ACTIVITY_IMAGE="$LOGO_SANTE"
    ACTIVITY_SUBTITLE="https://santefrd9.dev.klee.lan.net"
    ENV="RUI"
    JIRA_TITLE="Pensez √† mettre √† jour le statut de vos US Jira"
    JIRA_SUBTITLE="https://jira-santefr.rct01.kleegroup.com/secure/RapidBoard.jspa?rapidView=112&projectKey=SAN"

    if [ "$CI_JOB_STATUS" == "success" ]; then
      # Bracket encoding is required to be a valid OpenUri for Teams.
      US_LIST="https://gitlab-interne.dev.klee.lan.net/ASIP-Sante/Santefr-D9/-/merge_requests?scope=all&state=merged&deployed_after=$CI_JOB_STARTED_AT&not%5Blabel_name%5D%5B%5D=SAS&not%5Blabel_name%5D%5B%5D=RELEASE"
    fi
    ;;
  # SANTE RUE
  deploy_rue_sante)
    ACTIVITY_IMAGE="$LOGO_SANTE"
    ACTIVITY_SUBTITLE="https://d9-santefr.rct01.kleegroup.com"
    ENV="RUE"
    ;;
  # SAS RUI
  deploy_rui_sas)
    ACTIVITY_IMAGE="$LOGO_DRUPAL"
    ACTIVITY_SUBTITLE="https://sas.sas-dev.dev.klee.lan.net"
    ENV="RUI"
    ;;
  # SAS RUE
  deploy_rue_sas)
    ACTIVITY_IMAGE="$LOGO_DRUPAL"
    ACTIVITY_SUBTITLE="https://sas-d9-sasfr.rct01.kleegroup.com"
    ENV="RUE"
    ;;
  # KEYCLOAK RUI
  deploy_rui)
    ACTIVITY_IMAGE="$LOGO_KEYCLOAK"
    ACTIVITY_TITLE="Keycloak - $ACTIVITY_TITLE"
    ACTIVITY_SUBTITLE="https://seconnecter.santefr-keycloak-rui.dev.klee.lan.net"
    ENV="RUI"
    ;;
  # KEYCLOAK RUE
  deploy_rue)
    ACTIVITY_IMAGE="$LOGO_KEYCLOAK"
    ACTIVITY_TITLE="Keycloak - $ACTIVITY_TITLE"
    ACTIVITY_SUBTITLE="https://seconnecter-kc-santefr.rct01.kleegroup.com"
    ENV="RUE"
    ;;
  esac

  # If the deploy isn't manual.
  if [ "$CI_JOB_MANUAL" != "true" ]; then
    AUTHOR="GitLab"
    DEPLOY_TYPE="Automatique"
  fi
}

# Load JSON template and replace tokens.
# This allowed to use template in multiple tools (like Postman).
load_and_replace () {
  TEMPLATE=$(cat "$TEMPLATE")
  TEMPLATE="${TEMPLATE//\{\{activityImage\}\}/"$ACTIVITY_IMAGE"}"
  TEMPLATE="${TEMPLATE//\{\{activitySubtitle\}\}/"$ACTIVITY_SUBTITLE"}"
  TEMPLATE="${TEMPLATE//\{\{activityTitle\}\}/"$ACTIVITY_TITLE"}"
  TEMPLATE="${TEMPLATE//\{\{author\}\}/"$AUTHOR"}"
  TEMPLATE="${TEMPLATE//\{\{color\}\}/"$COLOR"}"
  TEMPLATE="${TEMPLATE//\{\{date\}\}/"$DATE"}"
  TEMPLATE="${TEMPLATE//\{\{deploy_type\}\}/"$DEPLOY_TYPE"}"
  TEMPLATE="${TEMPLATE//\{\{duration\}\}/"$DURATION"}"
  TEMPLATE="${TEMPLATE//\{\{env\}\}/"$ENV"}"
  TEMPLATE="${TEMPLATE//\{\{jiraTitle\}\}/"$JIRA_TITLE"}"
  TEMPLATE="${TEMPLATE//\{\{jiraSubtitle\}\}/"$JIRA_SUBTITLE"}"
  TEMPLATE="${TEMPLATE//\{\{job_url\}\}/"$CI_JOB_URL"}"
  TEMPLATE="${TEMPLATE//\{\{state\}\}/"$STATE"}"
  TEMPLATE="${TEMPLATE//\{\{us_list\}\}/"$US_LIST"}"
}

# Script.
if [[ ! -f "$TEMPLATE" ]] ; then
  #Exit if template doesn't exists.
  exit
fi
set_variables
load_and_replace
curl -X POST -H "Content-Type: application/json" -d "$TEMPLATE" "$WEBHOOK_URL"
